        public static int Jump1(int[] nums)
        {
            if (nums.Length == 1) return 0;
            int reach = 0;
            int nextreach = nums[0];
            int step = 0;
            for (int i = 0; i < nums.Length; i++)
            {
                nextreach = Math.Max(i + nums[i], nextreach);
                if (nextreach >= nums.Length - 1) return (step + 1);
                if (i == reach)
                {
                    step++;
                    reach = nextreach;
                }
            }
            return step;
        }

        public static int Jump(int[] nums)
        {
            done = false;
            step = 0;
            if (nums.Length <= 1)
            {
                return step;
            }
            Check(nums);           

            return step;
        }

        public static void Check(int[] nums)
        {
            if (done) return;
            var lastSteps = GetOneStepToEndPostion(nums);
            step++;
            if (lastSteps.Any(x => x == 0))
            {
                done = true;
                return;
            }
            else
            {
                foreach (var lastStep in lastSteps)
                {
                    var newNums = GetStepsInJump(nums, lastStep);
                    Check(newNums);
                }
            }
        }

        public static int[] GetStepsInJump(int[] nums, int end)
        {
            var result = nums.ToList();
            result.RemoveRange(end+1, nums.Length - 1 - end);

            return result.ToArray();
        }

        public static List<int> GetOneStepToEndPostion(int[] nums)
        {
            var end = nums.Length - 1;
            var result = new List<int>();
            for (var i = 0; i < nums.Length - 1; i++)
            {
                if (i + nums[i] >= end)
                {
                    result.Add(i);
                }
            }

            return result;
        }
    }
